import React, {useCallback, useEffect, useState} from "react";
import {deleteTodo, getTodo, getTodos, updateTodo, updateTodo2} from "../jobs";
import './todo.css'
import "antd/dist/antd.css";
import {Button, message, Modal} from "antd";


const Todo = (props) => {

    const [completed, setCompleted] = useState(false)
    const [loading, setLoading] = useState(false)
    const [visible, setVisible] = useState(false)
    const [edit, setEdit] = useState({title:'', id:''})
    useEffect(() => {
        getTodos()
    },[setCompleted])

    const handleComplete = (e) =>{
     let todo = e.target.value
        todo = JSON.parse(todo)
        todo.complete = !todo.complete
        console.log(todo.complete)
        updateTodo(todo).then(() => props.changeState(['new data']))
        message.info(todo.complete === false ? `${todo.title} Reset` : `${todo.title} Completed`)

    }
    const handelDelete = (e) => {
        deleteTodo(e.target.value).then(() => props.changeState(['deleted todo']))
        message.info("Task deleted")
    }

    const showModal = () => {
        setVisible(true)
    }

    const handleEdit = () => {
        showModal()
        console.log({...edit, id: props.id})
        updateTodo({...edit, title: edit.title, id:props.id}).then(() => props.changeState(['new data']))
        message.info("Task updated")
        setVisible(false);
}
    const handleCancel = () => {
        setVisible(false);
    }
    const handleModalSubmit = () => {

        handleEdit()

         //updateTodo(todo) //.then(() => props.changeState(['new data']))
    }




    return (
        <>
            <div className="todo">
                <h4>{props.title}</h4>
                <p>Complete: {props.complete ? 'Yes' : 'No' }</p>
                <section className="update">
                    <button className={"edit"} style={{backgroundColor: "#33bfff"}} onClick={showModal}>edit</button>
                    <button className={"complete-toggle"} style={props.complete ? {backgroundColor:"green"}: {backgroundColor:"red"}} value={props.todo} onClick={handleComplete}>{props.complete ? 'Done': 'complete task'}</button>
                    <button className={"delete"} value={props.id} onClick={handelDelete}>delete</button>

                    <Modal visible={visible} onOk={handleEdit} title={"Edit"} footer={null}>

                        <form onSubmit={handleModalSubmit} >

                            <input type="text" placeholder={"New Title"} value={edit.title} onChange={(e) => setEdit({...edit, title: e.target.value})} />
                        <Button className={'submit'} key="submit" type="primary"  onClick={handleEdit}>Submit</Button>
                            <Button className={'cancel'} key={'back'} onClick={handleCancel}> Cancel</Button>

                    </form>
                    </Modal>

                </section>
            </div>



        </>
    )
}

export default Todo
