import React, {useState} from "react";
import {getTodo, updateTodo, updateTodo2} from "../jobs";
import './todo.css'
const Todo = (props) => {

    const handleComplete = (e) =>{
     let todo = e.target.value
        todo = JSON.parse(todo)
        todo.complete = !todo.complete
        console.log(todo.complete)
        updateTodo(todo)


    }

    return (
        <>
            <div className="todo">
                <h4>{props.title}</h4>
                <p>Complete: {props.complete ? 'Yes' : 'No' }</p>
                <section className="update">
                    <button>edit</button>
                    <button value={props.todo} onClick={handleComplete}>{props.complete ? 'Done': 'complete task'}</button>
                </section>
            </div>



        </>
    )
}

export default Todo
