import React, {useCallback, useEffect, useState} from "react";
import {deleteTodo, getTodo, getTodos, updateTodo, updateTodo2} from "../jobs";
import './todo.css'
import {message} from "antd";


const Todo = (props) => {

    const [completed, setCompleted] = useState(false)
    useEffect(() => {
        getTodos()
    },[setCompleted])

    const handleComplete = (e) =>{
     let todo = e.target.value
        todo = JSON.parse(todo)
        todo.complete = !todo.complete
        console.log(todo.complete)
        updateTodo(todo).then(() => setCompleted(prevState => !prevState))
        console.log(completed)

    }
    const handelDelete = (e) => {
        deleteTodo(e.target.value)
        message.info("Task deleted")

    }



    return (
        <>
            <div className="todo">
                <h4>{props.title}</h4>
                <p>Complete: {props.complete ? 'Yes' : 'No' }</p>
                <section className="update">
                    <button>edit</button>
                    <button className={"complete-toggle"} style={props.complete ? {backgroundColor:"green"}: {backgroundColor:"red"}} value={props.todo} onClick={handleComplete}>{props.complete ? 'Done': 'complete task'}</button>
                    <button className={"delete"} value={props.id} onClick={handelDelete}>delete</button>

                </section>
            </div>



        </>
    )
}

export default Todo
